function PlotPoints(pts, values, range)% PLOTPOINTS - Plot points in 3d with associated colors.%   PlotPoints(pts, values, cmap, range)  figure  %  cmap = colormap;    if (nargin == 2)    range = [min(values), max(values)];  end  %  ncolor = size(cmap, 1);  npts = size(pts, 2);  %  rmin = range(1);  rmax = range(2);  delr = rmax - rmin;    %  hold on  %  for i=1:npts    color = (values(i) - rmin)/delr;    color = ceil(color*ncolor);    color = min(color, ncolor);	% keep in range    color = max(color, 1);      	% to prevent 0 index    plot3(pts(1, i), pts(2, i), pts(3, i), 'o', ...	  'MarkerFaceColor', cmap(color, :),    ...	  'MarkerEdgeColor','k',                ...	  'MarkerSize', 12);  end  %  axis equal  axis off  %%  axes('position', [0.0, 0.0, 0.25, 0.75]);%  caxis(gca, [rmin rmax]);  caxis(range)  colorbar('v')  %  hold off  %